function deleteUser(arr, id) {
  // Find the index
  const index = arr.findIndex((user) => user.id === id);

  // If there's no user with that id, -1 will be returned
  if (index === -1) {
    console.log('There is no user with this ID.');
    return arr; // Return the unchanged array
  }

  // If the user exists, remove them permanently
  arr.splice(index, 1);

  // Return the updated array
  return arr;
}

deleteUser(someDudes, '123_thatsallfolks');
